
public class MethodsofStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Firstname="Rafael";
		System.out.println(Firstname.endsWith("ael"));
		System.out.println(Firstname.endsWith("Rel"));
		System.out.println(Firstname.startsWith("Rafa"));
		System.out.println(Firstname.startsWith("fael"));
	}

}
